<script>
import Advantage from '../components/Advantage.vue';
export default {
  components: { Advantage },
    name: "romanov",
    transition() {
        if (!localStorage.fromTo) return 'slide-left';

        const { from, to } = JSON.parse(localStorage.fromTo);
        return to > from ? 'slide-left' : 'slide-right'
    },
    data() {
        return {
            leftOpen: false,
            rightOpen: false,
            currentNum: 0,
            advantages: [
                {
                    num: 1,
                    text: "Под золотым колпаком с отрывным кольцом пробка типа Камю с золотой шляпкой.",
                },
                {
                    num: 3,
                    text: "Герб Романовых и герб Бернадоттов.",
                },
                {
                    num: 5,
                    text: "Переосмысление формы бутылки — <br> отсылка к наследию.",
                },
                {
                    num: 7,
                    text: "Два вида золотой фольги и двойное конгревное тиснение.",
                },
            ],
            advRight: [
                {
                    num: 2,
                    text: "Символика солнца как один из узнаваемых атрибутов тронного зала царской семьи Романовых.",
                },

                {
                    num: 4,
                    text: "Дно с уникальным рельефным искрящимся рисунком с хрустальным переливом.",
                },
                {
                    num: 6,
                    text: "«Слепое» тиснение.",
                },
                {
                    num: 8,
                    text: "Стилизованный герб Российской империи, выполненный рельефом в стекле.",
                }
            ]
        }
    },
    methods: {
        toggle(direction) {
            switch (direction) {
                case 'right':
                    this.rightOpen = !this.rightOpen;
                    break;
                case 'left':
                    this.leftOpen = !this.leftOpen;
                    break;
            }

            // if (!this.leftOpen && !this.rightOpen) {
            //     this.currentNum = 0;
            // }
        },
        btnClick(num) {
            this.currentNum = num;
        },
    },
}
